mapper.insert:
WITH self_report_stats AS ( SELECT usrapp.owner, COALESCE(SUM(st.to_peer_traffic), 0) as sent_self, COALESCE(SUM(st.from_peer_traffic), 0) as recv_self FROM userapp usrapp JOIN swarm_tracker st ON st.userapps_id = usrapp.id WHERE st.first_time_seen >= #{startAt} AND st.last_time_seen <= #{endAt}
AND usrapp.owner IN
#{uid}
GROUP BY usrapp.owner ), distinct_ips AS ( SELECT DISTINCT ua.owner, uah.ip FROM userapp ua JOIN userapps_heartbeat uah ON uah.userapp_id = ua.id WHERE uah.last_seen_at >= #{startAt} AND uah.first_seen_at <= #{endAt}
AND ua.owner IN
#{uid}
), other_ack_stats AS ( SELECT di.owner, COALESCE(SUM(st.from_peer_traffic), 0) as sent_other, COALESCE(SUM(st.to_peer_traffic), 0) as recv_other FROM distinct_ips di JOIN swarm_tracker st ON st.peer_ip <<= di.ip::inet WHERE st.first_time_seen >= #{startAt} AND st.last_time_seen <= #{endAt} GROUP BY di.owner ) INSERT INTO user_swarm_statistic (user_id, sent_traffic_other_ack, received_traffic_other_ack, sent_traffic_self_report, received_traffic_self_report, last_update_at) SELECT usr.id, COALESCE(o.sent_other, 0), COALESCE(o.recv_other, 0), COALESCE(s.sent_self, 0), COALESCE(s.recv_self, 0), NOW() FROM "user" usr LEFT JOIN self_report_stats s ON s.owner = usr.id LEFT JOIN other_ack_stats o ON o.owner = usr.id WHERE 1=1
AND usr.id IN
#{uid}
ON CONFLICT (user_id) DO UPDATE SET sent_traffic_other_ack = EXCLUDED.sent_traffic_other_ack, received_traffic_other_ack = EXCLUDED.received_traffic_other_ack, sent_traffic_self_report = EXCLUDED.sent_traffic_self_report, received_traffic_self_report = EXCLUDED.received_traffic_self_report, last_update_at = EXCLUDED.last_update_at

mapper.select:
SELECT user_id, sent_traffic_self_report, sent_traffic_other_ack, received_traffic_other_ack, received_traffic_self_report, RANK() OVER ( ORDER BY ( (sent_traffic_other_ack * #{sentTrafficOtherAckWeight}) + (sent_traffic_self_report * #{sentTrafficSelfReportWeight}) + (received_traffic_other_ack * #{receivedTrafficOtherAckWeight}) + (received_traffic_self_report * #{receivedTrafficSelfReportWeight}) ) DESC ) AS rank FROM user_swarm_statistic
WHERE user_id NOT IN
#{ignoreUid}

WITH UserRanks AS ( SELECT user_id, sent_traffic_self_report, sent_traffic_other_ack, received_traffic_other_ack, received_traffic_self_report, RANK() OVER ( ORDER BY ( (sent_traffic_other_ack * #{sentTrafficOtherAckWeight}) + (sent_traffic_self_report * #{sentTrafficSelfReportWeight}) + (received_traffic_other_ack * #{receivedTrafficOtherAckWeight}) + (received_traffic_self_report * #{receivedTrafficSelfReportWeight}) ) DESC ) AS rank FROM user_swarm_statistic
WHERE user_id NOT IN
#{ignoreUid}
) SELECT * FROM UserRanks WHERE user_id = #{userId} LIMIT 1;
